using Marathon.Core.ViewModel.Base;
using Marathon.Core.ViewModel.BMICalculator.Models;

namespace Marathon.Core.ViewModel.BMICalculator.Design
{
    /// <summary>
    /// The design-time data for a <see cref="BMIResultViewModel"/>
    /// </summary>
    public class BMIResultDesignModel : BMIResultViewModel
    {
        #region Singleton

        /// <summary>
        /// A single instance of the design model
        /// </summary>
        public static BMIResultDesignModel Instance => new BMIResultDesignModel();

        #endregion

        #region Constructor

        /// <summary>
        /// Default constructor
        /// </summary>
        public BMIResultDesignModel()
        {
            Value = 24.2;
            Description = "Здоровый";
            Image = DesignModelHelpers.StringToByteArray("");

            Segments = new[]
            {
                new ScaleSegment(0, "FDC100", "Недостаточный вес"),
                new ScaleSegment(18.5, "00903E", "Здоровый "),
                new ScaleSegment(25, "FDC100", "Избыточный"),
                new ScaleSegment(30, "FF4747", "Ожирение"),
            };
        }

        #endregion
    }
}
